// eventemitter3.min.js - Minimal browser EventEmitter polyfill
(function(global){
  function EventEmitter(){this._e={};}
  EventEmitter.prototype.on=function(ev,fn){(this._e[ev]=this._e[ev]||[]).push(fn);return this;};
  EventEmitter.prototype.off=function(ev,fn){
    if(!this._e[ev])return this;
    if(!fn)this._e[ev]=[];else{var i=this._e[ev].indexOf(fn);if(i>-1)this._e[ev].splice(i,1);}
    return this;
  };
  EventEmitter.prototype.emit=function(ev){
    var args=[].slice.call(arguments,1),lst=this._e[ev]||[],i=0;
    for(;i<lst.length;i++)lst[i].apply(this,args);
    return this;
  };
  global.EventEmitter=EventEmitter;
})(typeof window!=='undefined'?window:this);
